import SongCard from "./../components/SongCard"
let alphabet = [
  "#",
  "A",
  "B",
  "C",
  "D",
  "E",
  "F",
  "G",
  "H",
  "I",
  "J",
  "K",
  "L",
  "M",
  "N",
  "Ñ",
  "O",
  "P",
  "Q",
  "R",
  "S",
  "T",
  "U",
  "V",
  "W",
  "X",
  "Y",
  "Z"
]

export function renderSongSections(songs) {
  return alphabet.map((letter) => {
    let songsFilteredByLetter = songs.filter((song) => song.name[0] === letter)

    if (songsFilteredByLetter.length) {
      return getSongSection(songsFilteredByLetter)
    }
  })
}

export function renderCategory(songs, id) {
  let songsFilteredByCategoryId = songs.filter((song) =>
    song.category.includes(id)
  )

  if (songsFilteredByCategoryId.length) {
    return getSongSection(songsFilteredByCategoryId)
  }
}

function getSongSection(songs, title) {
  return (
    <div key={`song-${index}`} className="row">
      <h1 id={title}>{title}</h1>
      <div className="col-12">
        <hr />
      </div>

      {songs.map((song, index) => {
        return <SongCard key={`song-${index}`} Song={song} />
      })}
    </div>
  )
}

export function drawAlphabet() {
  return alphabet.map((letter, index) => (
    <a key={`alphabet-${index}`} href={`#${letter}`}>
      {letter}
    </a>
  ))
}
