import SongCard from "./../components/SongCard"
let alphabet = [
    "#",
    "A",
    "B",
    "C",
    "D",
    "E",
    "F",
    "G",
    "H",
    "I",
    "J",
    "K",
    "L",
    "M",
    "N",
    "Ñ",
    "O",
    "P",
    "Q",
    "R",
    "S",
    "T",
    "U",
    "V",
    "W",
    "X",
    "Y",
    "Z"
  ]

export function drawSongsSection(songs) {
    return alphabet.map((letter) => {
      let songsFilteredByLetter = songs.filter((song) => song.name[0] === letter)

      if (songsFilteredByLetter.length) {
        getSongSection(songsFilteredByLetter)
      }
    })
  }

function getSongSection(songs, title) {
    return (
        <div className="row">
          <h1 id={title}>{title}</h1>
          <div className="col-12">
            <hr />
          </div>

          {songs.map((song) => {
            return <SongCard Song={song} />
          })}
        </div>
      )
}

export function drawAlphabet() {
    return alphabet.map((letter) => <a href={`#${letter}`}>{letter}</a>)
  }
