import React from "react"
import SearchBar from "./../components/SearchBar"
import SongCard from "./../components/SongCard"

function drawAlphabet() {
  let alphabet = [
    "#",
    "A",
    "B",
    "C",
    "D",
    "E",
    "F",
    "G",
    "H",
    "I",
    "J",
    "K",
    "L",
    "M",
    "N",
    "Ñ",
    "O",
    "P",
    "Q",
    "R",
    "S",
    "T",
    "U",
    "V",
    "W",
    "X",
    "Y",
    "Z"
  ]

  return alphabet.map((letter) => <a href={`#${letter}`}>{letter}</a>)
}

export default function Songs(props) {
  let { SongsList } = props

  return (
    <section className="container-md">
      <div className="row">
        <div className="col-12">
          <SearchBar />
        </div>
      </div>

      <div className="row">
        <div className="col-11 songs">
          <div className="row" id="songs">
            {Object.entries(SongsList).map((element) => (
              <SongCard Song={element[1]} />
            ))}
          </div>
        </div>

        <div className="col-1 position-fixed alphabet" id="alphabet">
          {drawAlphabet()}
        </div>
      </div>
    </section>
  )
}

function drawSongsSection(songs) {

    return alphabet.map(letter => {
        var songsFilteredByLetter = songs.filter(song => song.name[0] == letter);

        if (songsFilteredByLetter.length) {
            <div className="row">
                <h1 id="${letter}">{letter}</h1><div className="col-12"><hr /></div>
            </div>
            var content = '';

            songsFilteredByLetter.map(song => {
                content += `<div class="col-4 col-sm-3 col-lg-2 category">
             <a href="https://youtu.be/${song.youtubeId}?t=${song.youtubeTime}" target="_blank">   
                <img class="song-img" src="http://img.youtube.com/vi/${song.youtubeId}/mqdefault.jpg" width="100%">
            </a>
            <a href="https://youtu.be/${song.youtubeId}?t=${song.youtubeTime}" target="_blank">
                <p class="song-name">${song.name}</p>
            </a>
            </div>`;
            });

            songsSection.innerHTML += beginHTML + title + content + endHTML;

        }
    });
}
